mapper.sql:
		FROM codegroup
		WHERE 1=1

		 AND ifcgUseNy = #{shUseNy}
        AND ifcgDelNy = #{shDelNy}


            AND ifcgSeq = #{shValue}
            AND ifcgName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
	UPDATE codegroup 
    SET
		ifcgName=#{ifcgName},
        ifcgUseNy=#{ifcgUseNy}
	WHERE
		ifcgSeq=#{ifcgSeq}

mapper.insert:
INSERT INTO codegroup (

	ifcgUseNy,
	ifcgName	

)VALUES(
	#{ifcgUseNy},
	#{ifcgName}
)	

	SELECT last_insert_id()
