mapper.sql:
		FROM code AS a 
		left join codegroup AS b ON b.ifcgSeq = a.codeGroup_ifcgSeq
		WHERE 1=1
		 AND a.ifcdUseNy = #{shUseNy}
        AND a.ifcdDelNy = #{shDelNy}


            AND b.ifcgSeq = #{shValue}
            AND b.ifcgName LIKE CONCAT('%',#{shValue},'%')
            AND a.ifcdName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE code
SET
	ifcdName=#{ifcdName}
WHERE
	ifcdSeq=#{ifcdSeq}
