mapper.sql:
FROM user AS a left join code AS b ON a.genderCd =b.ifcdSeq WHERE 1=1 AND userDelNy = #{shDelNy}
AND userRegistrationDate BETWEEN #{shDateStart} AND #{shDateEnd} AND userModificationDate BETWEEN #{shDateStart} AND #{shDateEnd}
AND userName LIKE CONCAT('%',#{shValue},'%') AND userId LIKE CONCAT('%',#{shValue},'%') AND userEmail LIKE CONCAT('%',#{shValue},'%') AND userNickName LIKE CONCAT('%',#{shValue},'%')
